(529, 3, '2015-04-20 11:50:56', '2015-04-20 11:50:56', '[cmsmasters_row data_padding_bottom="220" data_padding_top="220" data_bg_size="cover" data_bg_attachment="scroll" data_bg_repeat="no-repeat" data_bg_position="top center" data_bg_img="10866|http://www.macromad.com.ar/wp-content/uploads/2015/04/02_f-1-300x169.png|medium" data_color="default" data_bot_style="default" data_top_style="default" data_width="boxed"][cmsmasters_column data_width="1/1"][cmsmasters_heading type="h1" font_size="60" line_height="70" font_weight="bold" font_style="normal" text_align="center" color="#ffffff" margin_top="0" margin_bottom="15" animation_delay="0"]Sobre nosotros[/cmsmasters_heading][cmsmasters_image align="none" animation_delay="0"][/cmsmasters_image][cmsmasters_divider width="short" height="0" style="solid" position="center" margin_top="0" margin_bottom="25" animation_delay="0"][cmsmasters_text animation_delay="0"]\r\n<p style="text-align: center;"><span style="color: #ffffff;"><span style="font-size: 14pt;">Tenemos todo lo que necesitás para concretar tus proyectos.\r\n</span></span><span style="color: #ffffff;"><span style="font-size: 14pt;">Vení a vivir una ''''experiencia MACROMAD''''</span></span></p>\r\n[/cmsmasters_text][/cmsmasters_column][/cmsmasters_row][cmsmasters_row data_width="boxed" data_color="first" data_bg_color="#f3c14b" data_padding_top="10" data_padding_bottom="0"][cmsmasters_column data_width="1/1"][cmsmasters_counters type="horizontal" count="4" icon_size="30" icon_space="60" icon_border_width="0" icon_border_radius="50%" animation_delay="0"][cmsmasters_counter subtitle="Más de 30 años de experiencia" value="30" value_suffix="+" icon_type="icon"]Experiencia[/cmsmasters_counter][cmsmasters_counter subtitle="Contamos con depósitos aptos para soportar grandes volúmenes" value="100" value_suffix="%" icon_type="icon"]Stock[/cmsmasters_counter][cmsmasters_counter subtitle="Tenemos una amplia variedad para ofrecerte" value="120" value_suffix="+" icon_type="icon